import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AccountService } from '../account.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  model: any = {};

  constructor(
    private router: Router, private userService: AccountService

  ) {}



  registerUser(){

  }
}

<h2>Register</h2>


  <div class="imgcontainer">
    <img src="../../assets/avatar.png" alt="Avatar" class="avatar">
  </div>

  <div class="container">
    <label for="uname"><b>Username</b></label>
    <input type="text" placeholder="Enter Username" name="username"    [(ngModel)]  = "model.username">

    <label for="psw"><b>Password</b></label>
    <input type="password" placeholder="Enter Password" name="password"  [(ngModel)]   = "model.password">

    <button type="submit" (click)="registerUser()">Login</button>
    <label>
      <input type="checkbox" checked="checked" name="remember"> Remember me
    </label>
  </div>

  <div class="container" style="background-color:#f1f1f1">
    <button type="button" class="cancelbtn">Cancel</button>
    <span class="psw">Forgot <a href="#">password?</a></span>
  </div>
