import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {
//para decir que no tienen valor definido-> ?
    name : string = "Pepe"
    email? : string
    pwd1? : string
    pwd2? : string

    Constructor(){
      this.email = "pepe@pepe.com"
    }
    ngOnInit(): void{

    }

    register(){
      //var todo el fichero
      //let dentro de este metodo
       let info = {
        name : this.name,
        email : this.email,
        pwd1 : this.pwd1,
        pwd2 : this.pwd2
       } 
       alert(JSON.stringify(info))


    }
}
<p>register works!</p>

<input type="text" [(ngModel)]='name' placeholder="nombre">
<br>
<input type="email" [(ngModel)]='email' placeholder="correo electronico">
<br>
<input type="pwd1" [(ngModel)]='pwd1' placeholder="contraseña">
<br>
<input type="pwd2" [(ngModel)]="pwd2" placeholder="confirme su contraseña">
<br>
<button (click)="register()"> crear Cuenta</button>
