import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { tap, map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class AccountService {

  private httpOptions;

  constructor(private router: Router, private httpClient : HttpClient) {

    this.httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json'
      })
    };

   }


   register(info: any) {
    this.httpClient.post("http://localhost:80/users/register", info, this.httpOptions)
    .subscribe(
      respuesta => {
        alert(respuesta);    
        console.log(respuesta);
      },
      error => {
        console.error(error);
      }
    );
  }



  async login(info: any): Promise<any> {
    try {
      const response = await this.httpClient.put("http://localhost:80/users/login", info, this.httpOptions).toPromise();
      console.log('Estado HTTP:', response.status);
      this.router.navigate(['/home']);
      return response;
    } catch (error) {
      console.error('Error en la solicitud:', error);
      alert('Credenciales incorrectas');
      throw error;
    }
  }




}
