import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AccountService {

  constructor(private httpClient : HttpClient) { }

  register(info: any) {
    this.httpClient.post("http://localhost:80/users/register", info)
    .subscribe(respuesta => {
      alert(respuesta)    
      console.log(respuesta)
      })

  }

  login(info: any) : Observable <any> {
    return this.httpClient.put("http://localhost:80/users/login", info)

  }

  getUserData(username: string,password: string){
    return this.httpClient.get('http://localhost:80/user/'+username+'/'+password);
  }

}
