import { Component } from '@angular/core';
import { AccountService } from '../account.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  name? : string;
  pwd1? : string;

  constructor(private accountService : AccountService){

  }
  ngOnInit(): void{

  }

  register(){
    let info = {
      name : this.name,
      pwd1 : this.pwd1
    }

    this.accountService.login(info);
  }
}
<p>login works!</p>

<input type="text" [(ngModel)]='name' placeholder="nombre">
<br>
<input type="email" [(ngModel)]='email' placeholder="correo electronico">
<br>
<input type="pwd1" [(ngModel)]='pwd1' placeholder="contraseña">
<br>
<input type="pwd2" [(ngModel)]="pwd2" placeholder="confirme su contraseña">
<br>
<button (click)="login()"> crear Cuenta</button>
