import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AccountService } from '../account.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit{

  model: any = {};

  constructor(
    private router: Router, private userService: AccountService

  ) {}

  ngOnInit() {
  }


  registerUser(){

    const registerData = {
      name: this.model.username,
      email: this.model.email,
      pwd: this.model.password,
      cpwd: this.model.cpassword
    };
  }
}

<h2>Register</h2>



  <div class="container">
    <label for="uname"><b>Name</b></label>
    <input type="text" placeholder="Enter Username" name="username"    [(ngModel)]  = "model.username">

    <label for="email"><b>Email</b></label>
    <input type="text" placeholder="Enter Email" name="email"    [(ngModel)]  = "model.email">

    <label for="psw"><b>Password</b></label>
    <input type="password" placeholder="Enter Password" name="password"  [(ngModel)]   = "model.password">

    <label for="cpsw"><b>Confirm Password</b></label>
    <input type="password" placeholder="Enter Password again ..." name="cpassword"  [(ngModel)]   = "model.cpassword">

    <button type="submit" (click)="registerUser()">Register</button>

  </div>

  <div class="container" style="background-color:#f1f1f1">
    <button type="button" class="cancelbtn">Cancel</button>
  </div>
